	/**
	 * This method obtains all the events associated with a specific EventScenario from the database,
	 * it then creates the events stores them in a list and returns the list.
	 * This method should be called when setting up the Event Handler
	 * @param scenario the id number for a specific Scenario to be queried
	 * @return a List of Events that makes up the requested Scenario
	 */
	public List<Event> getEvents(int scenario){
		List<Event> Events = new LinkedList<Event>();

		String Query = "SELECT * FROM Event WHERE sessionID = " + scenario + " ORDER BY start_time";
		System.out.println("Query= " + Query);

		System.out.println("GETTING EVENTS");
		if(mock)
		{
			System.out.println("GETTING MOCKED EVENTS");
			//mock events
			//Synch=S
			Event ne = new Event("1","1","1955","370370.5334, 3497869.6521","5","1","ST","yes","no reason, random","1");
		}
		else
		{
			try{
				java.sql.Statement stmn = conexion.createStatement();
	
				ResultSet res = stmn.executeQuery(Query);
				System.out.println("Query Succesful");
				System.out.println(res.toString());
	
				while(res.next()){
	
					System.out.println(">>"+res.getInt(1)+"|" +res.getInt(8)+"|"+res.getInt(5)+"|"+res.getString(6)+"|"+res.getInt(7)+"|"+res.getInt(2)+"|"+
							res.getString(10)+"|"+res.getInt(3)+"|"+res.getString(4)+"|"+res.getInt(9));
					
					try
					{
					Event ne = new Event(res.getInt(1)+"",res.getInt(8)+"",res.getInt(5)+"",res.getString(6)+"",res.getInt(7)+"",res.getInt(2)+"",
							res.getString(10)+"",res.getInt(3)+"",res.getString(4)+"",res.getInt(9)+"");
					
					System.out.println("<<"+ ne.toString());
					Events.add(ne);
					}
					catch(Exception ee)
					{
						System.out.println("Shoot!");
						ee.printStackTrace();
					}
				}
				
				return Events;
	
			}catch (SQLException e){
				e.printStackTrace();
			}catch (Exception e){
				e.printStackTrace();
			}
		}
		LinkedList empty = new LinkedList();
		return empty;
	}
	/**
	 * Updates the EventSessionID in the databse
	 * @param sessionID new session id
	 */
	public void setEventSessionID(int sessionID)
	{
		String query = "UPDATE Event SET sessionID = " + sessionID + " WHERE sessionID = -1";
		
		try{
			java.sql.Statement stmn = conexion.createStatement();
			stmn.executeUpdate(query);
			
		}catch (SQLException e){
			e.printStackTrace();
		}catch (Exception e){
			e.printStackTrace();
		}
	}
